        return new Source("{% extends 'base.html.twig' %}

{% block body %}
    <h1>All TV shows: </h1>
    {% for program in programs %}
        <div>
            <h2>
                {{ loop.index }} / {{ program.title }} - Category: {{ program.category.name }}
            </h2>
            <p>{{ program.synopsis }}</p>
        </div>
    {% else %}
        No TV show found.
    {% endfor %}
    <a href=\"{{ path('app_index') }}\">
        Back to home
    </a>
    {{ form_start(form) }}
    <button type=\"submit\" name=\"btnSearch\">Rechercher</button>
    {{ form_end(form) }}
        

{% endblock %}", "wild/index.html.twig", "/home/lirith-drasar/Bureau/Projet/wild-series/templates/wild/index.html.twig");
